mapper.update:
update blog_post set
id=#{id}

	,title=#{title}


	,excerpt=#{excerpt}


	,content=#{content}


	,type=#{type}


	,tagName=#{tagName}


	,categoryId=#{categoryId}


	,pstatus=#{pstatus}


	,cstatus=#{cstatus}


	,ccount=#{ccount}


	,weekccount=#{weekCCount}


	,weekrcount=#{weekRCount}


	,creatorId=#{creatorId}


	,creator=#{creator}


	,createTime=#{createTime}


	,lastUpdate=#{lastUpdate}


	,keywords=#{keywords}


	,description=#{description}


where id=#{id}

update blog_post set
ccount = ccount + 1
where id=#{id}

update blog_post set
rcount = rcount + 1
where id=#{id}
