mapper.select:
select ssj.id, ssj.templateId, ssj.storyId, ssj.categoryId, bc.TITLE as categoryName, ssj.userId, ssj.username, ssj.name, ssj.url, ssj.title, ssj.status, ssj.spiderStatus, ssj.qtRule, ssj.creator, ssj.createTime, ssj.lastUpdate from spider_story_job ssj left join bbs_category bc on bc.ID = ssj.categoryId where 1=1
and ssj.title like #{query.title}
order by ssj.id desc

mapper.update:
		update spider_story_job set
		id=#{id}
		
		
			,categoryId=#{categoryId}
		
		
			,templateId=#{templateId}
		
		
			,storyId=#{storyId}
		
		
			,name=#{name}
		
		
			,url=#{url}
		
		
			,title=#{title}
		
		
			,status=#{status}
		
		
			,spiderStatus=#{spiderStatus}
		
		
			,qtRule=#{qtRule}
		
		
			,lastUpdate=#{lastUpdate}
		

		where id=#{id}
