mapper.update:
update story_detail set
id=#{id}

	,storyId=#{storyId}


	,title=#{title}


	,content=#{content}



	,partId=#{partId}


	,partTitle=#{partTitle}


	,status=#{status}


	,picPath=#{picPath}


	,readCount=#{readCount}


	,replyCount=#{replyCount}


	,crawlUrl=#{crawlUrl}


	,lastUpdate=#{lastUpdate}

where id=#{id}
