mapper.select:
select si.id, si.categoryId, si.categoryName, si.title, si.outline, si.author, si.status, si.picPath, si.lastDetailId, si.lastDetailTitle, si.tagName, si.likeCount, si.readCount, si.replyCount, si.sortIndex, si.remark, si.crawlUrl, si.lastUpdateDetail, si.creator, si.createTime, si.lastUpdate from story_info si where 1=1
and categoryId=#{query.categoryId}
order by sortIndex desc, id desc

mapper.update:
update story_info set
id=#{id}

	,categoryId=#{categoryId}


	,categoryName=#{categoryName}


	,title=#{title}


	,author=#{author}


	,status=#{status}


	,picPath=#{picPath}



	,outline=#{outline}


	,lastDetailId=#{lastDetailId}


	,lastDetailTitle=#{lastDetailTitle}


	,tagName=#{tagName}


	,likeCount=#{likeCount}


	,readCount=#{readCount}


	,replyCount=#{replyCount}


	,sortIndex=#{sortIndex}


	,remark=#{remark}


	,crawlUrl=#{crawlUrl}


	,lastUpdate=#{lastUpdate}


	,keywords=#{keywords}


	,description=#{description}


	,lastUpdateDetail=#{lastUpdateDetail}

where id=#{id}

mapper.select:
select si.id, si.categoryId, si.categoryName, si.title, si.outline, si.author, si.status, si.picPath, si.lastDetailId, si.lastDetailTitle, si.tagName, si.likeCount, si.readCount, si.replyCount, si.sortIndex, si.remark, si.crawlUrl, si.lastUpdateDetail, si.creator, si.createTime, si.lastUpdate from story_info si where 1=1
and si.categoryId = #{categoryId}
order by si.createTime desc LIMIT #{topN}

