mapper.update:
update story_info set
id=#{id}

	,categoryId=#{categoryId}


	,title=#{title}


	,author=#{author}


	,status=#{status}


	,picPath=#{picPath}



	,outline=#{outline}


	,lastDetailId=#{lastDetailId}


	,lastDetailTitle=#{lastDetailTitle}


	,tagName=#{tagName}


	,likeCount=#{likeCount}


	,readCount=#{readCount}


	,replyCount=#{replyCount}


	,sortIndex=#{sortIndex}


	,remark=#{remark}


	,crawlUrl=#{crawlUrl}


	,lastUpdate=#{lastUpdate}

where id=#{id}
