mapper.select:
select std.id, std.storyId, std.topicId, std.sortIndex, std.creator, std.createTime, std.lastUpdate, si.title, si.author, si.categoryId, si.categoryName, si.lastDetailId, si.lastUpdateDetail, si.picPath, si.outline, si.readCount, si.replyCount, si.likeCount from story_topic_detail std left join story_info si on std.storyId = si.id where 1=1
,std.topicId = #{query.topicId}
order by std.id desc

mapper.update:
update story_topic_detail set
id=#{id}

	,storyId=#{storyId}


	,sortIndex=#{sortIndex}


	,lastUpdate=#{lastUpdate}

where id=#{id}
