mapper.select:
select
id, storyId, title, userId, username, readDetailId, readDetailTitle, creator, createTime, lastUpdate
from story_user_read where id = #{id}

mapper.update:
update story_user_bookshelf set
id=#{id}

	,storyId=#{storyId}


	,title=#{title}


	,userId=#{userId}


	,username=#{username}


	,readDetailId=#{readDetailId}


	,readDetailTitle=#{readDetailTitle}


	,lastUpdate=#{lastUpdate}

where id=#{id}
